window.onload=function(){var o=document.getElementById("btn");var e=document.documentElement.clientHeight;var n=null;var l=true;window.onscroll=function(){var t=document.documentElement.scrollTop||document.body.scrollTop;if(t>=e){o.style.display="block"}else{o.style.display="none"}if(!l){clearInterval(n)}l=false};o.onclick=function(){n=setInterval(function(){var o=document.documentElement.scrollTop||document.body.scrollTop;var e=Math.floor(-o/6);document.documentElement.scrollTop=document.body.scrollTop=o+e;l=true;if(o==0){clearInterval(n)}},30)}};